<?php	require 'constants.php';	//unset($_COOKIE['pcii']); // Used for DEBUGGING, somtimes?><html>	<style type="text/css">		@import url(jquery-ui.css );		@import url(main.css);	</style><head>	<meta http-equiv="content-type" content="text/html;charset=UTF-8">	<title>Welcome</title>	<script type="text/javascript" src="jquery-1.10.1.js"></script>	<script type="text/javascript" src="jquery-ui.js"></script>	<script type="text/javascript" src="common.js"></script></head><body id='body'>	<h3>Welcome</h3>	<span>server state: <?php var_dump($state); ?><span><br><br>	<span>Username: <input type="text" id="usernameId" name="username"></span><br>	<button id="continue" >Yes, I'll Code!</button>	<button id="watch">Err,Can I Just Watch?</button>	<br>	<br><script type="text/javascript">// JavaScript Globals// Functions function saveStateToCookie() {	console.log("Cookie is :...");	console.log(state);	document.cookie = "pcii=" + encodeURIComponent(JSON.stringify(state));}function nameSet() {	var nameEl = document.getElementById("usernameId");	state['name'] = nameEl.value;	saveStateToCookie();}// Handlers$(document).ready(function () {	$('#continue').click( function() {		nameSet();		location.href = href("coder");	});	$('#watch').click( function() {		location.href = href("replay");	});	$('input#usernameId').keyup(function(evt) {		//console.log(evt.target.id);		var el = document.getElementById(evt.target.id);		$('#continue').button("option", "disabled", el.value === "");	});});// INITvar state = eval(decodeURIComponent(document.cookie));console.log("cookie/state=...");console.log(state);$('button').button();if (state['name'] && state['name'].length > 0) {	var nameEl = document.getElementById("usernameId");	nameEl.value = state['name'];	$('#continue').button("option", "disabled", false );} else {	$('#continue').button("option", "disabled", true );}</script></body></html>